// import {ReactComponent as ReactLogo} from './images/cloud.svg';
import { useState } from 'react';
import './styles.css';
import cloud from './images/cloud.svg';

function App() {
  const [count, setCount] = useState(0);
  const [arr, setArr] = useState(['hello', 'there']);
  const [todos, setTodos] = useState([{ category: 'task', text: 'Learn Hooks' }]);

  return (
    <div className="App">
      <header className="App-header">
        <img src={cloud} className="cloud" alt="cloud" />
        <p>
          Number of clouds: {count}
        </p>
        <button onClick={() => setCount(count + 1)}>
          Increment
        </button>
        <button onClick={() => setArr(arr => [...arr, 'linh'])}>
          Reset
        </button>

        <p>
          Number of clouds: {arr.join(' ')}
        </p>
        <p>
          todos: {arr.join(' ')}
        </p>
      </header>
    </div>
  );
}

export default App;
