// import {ReactComponent as ReactLogo} from './images/cloud.svg';
import { useState, useEffect } from 'react';
import './styles.css';
import cloud from './images/cloud.svg';

const Emoji = ({symbol, label}) => (
  <span
      className="emoji"
      role="img"
      aria-label={label ? label : ""}
      aria-hidden={label ? "false" : "true"}
  >
      {symbol}
  </span>
);

function App() {
  const [count, setCount] = useState(0);
  const [arr, setArr] = useState(['hello', 'there']);
  const [todos, setTodos] = useState([{ emoji: 'sushi', text: 'Learn Hooks' }]);

  useEffect(() => {
    document.title = `You clicked ${count} times`;
  });

  return (
    <div className="App">
      <header className="App-header">
        <img src={cloud} className="cloud" alt="cloud" onClick={() => {
            setCount(count + 1);

            }
          }/>
        <p>
          Number of clouds: {count}
        </p>
        <button onClick={() => setCount(count + 1)}>
          Increment
        </button>
        <button onClick={() => setCount(0)}>
          {/* onClick={() => setArr(arr => [...arr, 'linh'])}> */}
          Reset
        </button>

        <p>
          Number of clouds: {arr.join(' ')}
        </p>
        <p>
          <Emoji symbol="🐑" label="sheep"/>
          {/* todos: {todos.join(' ')} */}
        </p>
      </header>
    </div>
  );
}

export default App;
