      reflectedImage: Math.random() < 0.5,
      // emoji:
    });
  }
  return clouds
};

function App() {
  const [count, setCount] = useState(0);
  // const [arr, setArr] = useState(['hello', 'there']);
  // const [todos, setTodos] = useState([{ emoji: 'sushi', text: 'Learn Hooks' }]);
  const [running, setRunning] = useState(1)
  const clouds = generateClouds();
  console.log(clouds);
  useEffect(() => {
    document.title = `You clicked ${count} times`;
  });

  return (
    <div className="App">
      <header className="App-header">
        <p>
          Number of clouds: {count}
        </p>
        <button onClick={() => {
          setCount(0);
          setRunning(1);
        }}>
          Start over
        </button>

        <p>
          You're doing great! <Emoji symbol="ðŸ˜…" label="nervous-laugh"/>
        </p>
        <img 
          src={cloud} 
          className="cloud"
          alt="cloud" 
          onAnimationEnd={() => setRunning(0)}
          running={running} 
          onClick={() => {
            setCount(count + 1);
            setRunning(0)
            }
          }
        />
      </header>
    </div>
  );
}

export default App;
